```
/****/
function shallowClone(source) {
    if (!source || typeof source !== 'object') {
        throw new Error('error arguments');
    }
    var targetObj = source.constructor === Array ? [] : {};
    for (var keys in source) {
        if (source.hasOwnProperty(keys)) {
            targetObj[keys] = source[keys];
        }
    }
    return targetObj;
}

/**/
function ProxyCopy(target,handle){
  var targetCopy = shallowClone(target);
  Object.keys(targetCopy).forEach(function(key){
    Object.defineProperty(targetCopy, key, {
      get: function() {
        return handle.get && handle.get(target,key);
      },
      set: function(newVal) {
        handle.set && handle.set();
        target[key] = newVal;
      }
    });
  })
  return targetCopy;
}

var person = {name:''};
var personCopy = new ProxyCopy(person,{
  get(target,key){
    console.log('get');
    return target[key];
  },
  set(target,key,value){
    console.log('set')
    // return true;
  }
})
person.name = 'arvin';  // 
personCopy.name = 'arvin';  // set
console.log(person.name);   // 
console.log(personCopy.name);   // get

```



